import React from 'react';
import IconLink from "./IconLink";
import { Box } from "@mui/material";

function PortfolioBlock(props) {
    const { image, source, title, tech1, tech2 } = props;

    return (
        <Box display="flex" flexDirection="column" alignItems="center" p={2} m={2} boxShadow={3}>
            <Box style={{ flex: '1 0 auto', maxWidth: '33.33%' }}>
                <img src={image} alt="mockup" style={{ width: '100%', height: 'auto' }} />
            </Box>
            <Box style={{ flex: '2 0 auto', width: '100%' }}>
                <h1 style={{ fontSize: '2rem', textAlign: 'center' }}>{title}</h1>
                <Box className="portfolio" display="flex" flexDirection="column" gap={'1.5rem'} alignItems="center" fontSize="1.5rem" py="2rem">
                    <Box p={1} border="2px solid black" borderRadius="25px" style={{ background: 'linear-gradient(135deg, rgb(95, 92, 255), rgb(168, 215, 255))' }}>
                        <p>{tech1}</p>
                    </Box>
                    {tech2 && (
                        <Box p={1} border="2px solid black" borderRadius="25px">
                            <p>{tech2}</p>
                        </Box>
                    )}
                    <Box p={1} border="2px solid black" borderRadius="25px">
                        <IconLink link={source} title="Source Code" icon="fa fa-code" />
                    </Box>
                </Box>
            </Box>
        </Box>
    );
}

export default PortfolioBlock;
