import React from 'react';
import IconLink from "./IconLink";
import {Box} from "@mui/material";

function PortfolioBlock(props) {
   const {image, source, title, tech1, tech2} = props;

   const renderTech2 = tech2 ? (
      <>
         <span style={{ margin: '0 10px' }}></span>
         <Box p={1} border={'2px solid black'} borderRadius={'25px'} style={{ background: 'linear-gradient(135deg, rgb(95, 92, 255), rgb(168, 215, 255))'}}>
            <p>{tech2}</p>
         </Box>
      </>
   ) : null;
   return (
      <Box display={'flex'} flexDirection={'row'} justifyContent={'center'} alignItems={'center'}>
         <Box component={'img'} src={image} alt={'mockup'}/>
         <Box display={'flex'} flexDirection={'column'} justifyContent={'center'} alignItems={'center'}>

            <h1 style={{fontSize: '2rem'}}>{title}</h1>
            <Box className={'portfolio'} display={'flex'} flexDirection={'column'} gap={'1.5rem'}
               alignItems={'center'} fontSize={'1.5rem'} py={'2rem'}>
               <Box display={'flex'} flexDirection={'line'}>
               <Box p={1} border={'2px solid black'} borderRadius={'25px'} style={{ background: 'linear-gradient(135deg, rgb(95, 92, 255), rgb(168, 215, 255))'}}>
                     <p>{tech1}</p>
                  </Box>
                  {renderTech2}
               </Box>
               <Box p={1} border={'2px solid black'} borderRadius={'25px'}>
                  <IconLink link={source} title={'Source Code'} icon={'fa fa-code'}/>
               </Box>
            </Box>
         </Box>
      </Box>
   );
}

export default PortfolioBlock;
